mapper.select:
SELECT
    SCHDL_ID schdlId
FROM GS_SEND_SCHDL
WHERE MASTER_SCHDL_ID = ${schdlId}
AND SEND_TYPE='R_S'
AND DATE_FORMAT(RESERVE_DATE, '%Y%m%d') = DATE_FORMAT(SYSDATE(), '%Y%m%d');

mapper.update:
UPDATE GS_SEND_SCHDL
SET TARGET_CNT = TARGET_CNT + ${targetCnt},
    SEND_CNT = SEND_CNT + ${sendCnt},
    SUCCES_CNT = SUCCES_CNT + ${succesCnt},
    FAIL_CNT = FAIL_CNT + ${failCnt}
WHERE SCHDL_ID = ${schdlId}

UPDATE GS_SEND_QUE_RAW
SET SEND_FLAG = #{sendFlag},
    RESULT_CD = #{resultCd}
WHERE UUID = #{uuid}
AND SEND_GUBUN = 'M'
AND SEND_FLAG = '0000'
